mapper.select:
select count(userid)
from member
where userid = #{userid}

select username, userid, userpwd
from member
where userid = #{userid} and userpwd = #{userpwd}

mapper.update:
update member
userpwd = #{userpwd}, username = #{username}, email = #{email},
where userid = #{userid}

mapper.select:
select userid, username, email, joindate
from member
where userid = #{userid}
